import { Component, OnInit } from '@angular/core';
import {Observable} from "rxjs";
import {User} from "../models/user";
import {UserService} from "../user-service/user-service.component";

@Component({
  selector: 'app-menu',
  templateUrl: './menu.component.html',
  styleUrls: ['./menu.component.css']
})
export class MenuComponent implements OnInit {

  constructor(public user: User, public userService: UserService) { }

  public validToken$: Observable<boolean>;
  ngOnInit(): void {
    this.validToken$ = this.userService.validToken();
    this.validToken$.subscribe();
    this.userService.getUser().subscribe((user) => this.user = user);
  }

}
<nav class="navbar navbar-expand-lg navbar-light bg-light">
  <a a class="navbar-brand" href="#" [routerLink]="['']" ><img width="100px" src="assets/images/minet.png"></a>
  <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNavDropdown" aria-controls="navbarNavDropdown" aria-expanded="false" aria-label="Toggle navigation">
    <span class="navbar-toggler-icon"></span>
  </button>
  <div class="collapse navbar-collapse" id="navbarNav">
    <ul class="navbar-nav mr-auto">
      <li class="nav-item">
        <a href="#" class="nav-link" [routerLink]="['charteminet']" [routerLinkActiveOptions]="{exact: true}" [routerLinkActive]="['active', 'text-primary']">Charte MiNET</a>
      </li>
      <li class="nav-item">
        <a href="#" class="nav-link" [routerLink]="['chartehosting']" [routerLinkActiveOptions]="{exact: true}" [routerLinkActive]="['active', 'text-primary']">Charte Hosting</a>
      </li>
      <li class="nav-item">
        <a href="#" class="nav-link" *ngIf="!(validToken$ | async)" (click)="userService.login()">Login</a>
      </li>

      <li class="nav-item">
        <a class="nav-link" href="#" *ngIf="(validToken$ | async)" (click)="userService.logout()">Logout</a>
      </li>
    </ul>
  </div>
</nav>
